import React, { useState, useEffect } from 'react';
import '../Carousel.css';

const Carousel = ({ items }) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [rotationAngle, setRotationAngle] = useState(0);
  
  useEffect(() => {
    // Рассчитываем угол поворота для центрирования активного элемента
    const angle = -(activeIndex * (360 / items.length));
    setRotationAngle(angle);
  }, [activeIndex, items.length]);

  const handleClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <div className="carousel">
      <div 
        className="carousel-container"
        style={{ 
          transform: `rotateY(${rotationAngle}deg)`,
          transition: 'transform 0.8s cubic-bezier(0.645, 0.045, 0.355, 1)'
        }}
      >
        {items.map((item, index) => {
          // Рассчитываем угол для каждого элемента
          const angle = (index * (360 / items.length));
          const z = 300; // Радиус круга
          
          return (
            <div 
              key={item.id}
              className={`carousel-item ${index === activeIndex ? 'active' : ''}`}
              style={{ 
                transform: `rotateY(${angle}deg) translateZ(${z}px)`,
                transformStyle: 'preserve-3d'
              }}
              onClick={() => handleClick(index)}
            >
              <img src={item.image} alt={item.title} />
              <div className="carousel-info">
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <p className="price">{item.price}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Carousel;
