import React, { useState } from 'react';
import Carousel from './components/Carousel';
import './App.css';

function App() {
  const desserts = [
    {
      id: 1,
      title: "Чизкейк Нью-Йорк",
      description: "Нежный сливочный чизкейк на песочной основе",
      price: "1200 руб/кг",
      image: "/photo_2025-09-02_18-50-35.jpg"
    },
    {
      id: 2,
      title: "Тирамису",
      description: "Классический итальянский десерт с кофейным вкусом",
      price: "1400 руб/кг",
      image: "/photo_2025-09-02_18-50-45.jpg"
    },
    {
      id: 3,
      title: "Медовик",
      description: "Нежные медовые коржи со сметанным кремом",
      price: "1000 руб/кг",
      image: "/photo_2025-09-02_18-50-55.jpg"
    },
    {
      id: 4,
      title: "Макаруны",
      description: "Французское пирожное с разнообразными начинками",
      price: "150 руб/шт",
      image: "/photo_2025-09-02_18-50-58.jpg"
    }
  ];

  return (
    <div className="App">
      <header>
        <h1>Вкуснейшие десерты ручной работы</h1>
        <p>Свежие домашние десерты на заказ с доставкой</p>
      </header>

      <Carousel items={desserts} />

      <footer>
        <p>© {new Date().getFullYear()} Домашние десерты. Все права защищены.</p>
      </footer>
    </div>
  );
}

export default App;
